import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {useState} from 'react'
import {Link, Redirect} from 'react-router-dom'
import {Breadcrumb, BreadcrumbItem, Card, CardBody, CardHeader, Media} from 'reactstrap';
import {Button, Label, Col, Row} from 'reactstrap'

const Sidebar = () => {
    const [search, setSearch] = useState("")

    const handleChange = (event) => {
        setSearch(event.target.value)
    }

    const handleSubmit = (event) => {

        fetch('http://localhost:8983/solr/businessyelp/select?q=' + search)
            .then(response => {
                if (response.status >= 200 && response.status < 300) {
                    return response
                } else {
                    console.log("something happened wrong")
                }
            })
            .then(response => response.json())
            .then(response => {
                let a = []
                a.push(response["response"]["docs"])
                console.log(a)
                props.setResponse(response["response"]["docs"])
                // console.log(response)
            })
            .catch(err => err);

        event.preventDefault()
    }

  return (
    <div class="">
      <div class="row ">
        <h2 className="p-4">Bukit Vista</h2>
      </div>
      <div class="row ">
        <div
          style={{ height: "100vh", background: "#2e053b", color: "white" }}
          class="col-md-2 full-height "
        >
          <h4 className="p-5" style={{ color: "white" }}>
            Guest Portal
          </h4>
        </div>
        <div
          style={{ height: "100vh" }}
          class="col-md-10 d-flex justify-content-center "
        >
          <div className="mt-5">
            <h5>Your booking code</h5>
           
            <form onSubmit={handleSubmit}>
                <Row className="form-group mt-4">
                    <Label className="text-center fs-4" htmlFor="firstname" md={2}>Search </Label>
                    <Col md={8}>
                        <input type="text" className="form-control " type="text" name="search"
                               onChange={handleChange}/>
                    </Col>
                    <Col md={2}>
                        <input className="btn btn-primary" type="submit" value="submit"/>
                    </Col>
                </Row>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
