import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import data from "../../languageProvider/locales/en_US.json";

class Sidebar extends Component {
  constructor(props) {
    super(props);
    this.state = { username: "" };
    this.state = { alert: "" };
  }
  myChangeHandler = (event) => {
    this.setState({ username: event.target.value });
    var patt = new RegExp("[A-Z]");
    let inputtext = event.target.value;
    this.setState({ alert: "Should be combination of numbers & alphabets" });

    if(inputtext>12){

        this.setState({ alert: "Should be combination of numbers & alphabets" });


    }

  };

  render() {
    return (
      <div class="">
        <div class="row ">
          <h2 className="p-4">Bukit Vista</h2>
        </div>
        <div class="row ">
          <div
            style={{ height: "100vh", background: "#2e053b", color: "white" }}
            class="col-md-2 full-height "
          >
            <h4 className="p-5" style={{ color: "white" }}>
              {data.menu}
            </h4>
          </div>
          <div
            style={{ height: "100vh" }}
            class="col-md-10 d-flex justify-content-center "
          >
            <div className="col-md-6 mt-5">
              <div className="col-md-12">
    <h5 className="text-center">{data.prompttext}{this.state.alert}</h5>
              </div>
              <br></br>
              <div class="form-group col-md-12">
                <input
                  type="text"
                  class="form-control"
                  placeholder="KJSH87HGDK"
                  onChange={this.myChangeHandler}
                />
              </div>
            </div>

            {/* <div className="">
            <h5>Your booking code</h5>

            <div class="form-group w-75">
              <input
                type="text"
                class="form-control"
                id="example3"
                placeholder="75% width of the parent"
              ></input>
            </div>
          </div> */}
          </div>
        </div>
      </div>
    );
  }
}

export default Sidebar;
