import React, { useState, useEffect } from "react";
import axios from "axios";

function DataFetching(props) {
  const [search, setSearch] = useState([]);
  console.log("Here" + props.serach);

  useEffect(() => {
    axios
      .get(
        "https://bv-online-assessment.herokuapp.com/api/bookings/" +
          props.serach
      )
      .then((res) => {
        console.log(res);
        setSearch(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div className="row">
      <div className="col-md-1"></div>
      <div className="col-md-11">
      <img src={serach.profile_picture} class="rounded float-left" />
        <p>Hi, {search.guest_name}</p>
        <br></br>
        <p>
          Thank you for booking with Bukit Vista. Here are the details of your
          current booing
        </p>
        <br></br>
        <div className="row">
          <div className="col-md-2">
            <p>Property name:</p>
          </div>
          <div className="col-md-9">
          <p ><strong>{search.property_name}</strong></p>
        </div>
        </div>
        <br></br>
        <div className="row">
          <div className="col-md-2">
            <p>Check in date:</p>
          </div>
          <div className="col-md-2">
          <p ><strong>{search.check_in_date}</strong></p>
        </div>
        <div className="col-md-2">
            <p>Check out date:</p>
          </div>
          <div className="col-md-2">
          <p ><strong>{search.check_out_date}</strong></p>
        </div>
        </div>
        <br></br>
        <div className="row">
          <div className="col-md-2">
            <p>Arrival time:</p>
          </div>
          <div className="col-md-9">
          <p >--:--(Please set your arrival time)</p>
        </div>
        </div>
        
      </div>
    </div>
  );
}

export default DataFetching;
